import './App.css';
import axios from "axios";
import { useState } from 'react';

function App() {

  const [campos, setCampos] = useState({
    txtNome: "",
  })

  const [ret, setRet] = useState("")

  const handleInputChange = (ev) => {
    if (ev === undefined) {
      console.log('evento undefined')
    } else {
      campos[ev.target.name] = ev.target.value
      console.log(ev.target.name)
      setCampos(campos) 
    }
  }

  const handleFormSubmit = (ev) =>{
    ev.preventDefault();
    console.log(campos);
    axios.post('http://localhost:5000//api/say_name5', campos).then(response => {
        setRet(response.data.first_name);
    })
}


return (
  <div>
      <form onSubmit={handleFormSubmit}>
          <fieldset>
              <legend>
                  <h2>Dados de Cadastro</h2>
              </legend>

              <div>
                  <label>Nome:
                      <input type="text" name="txtNome" id="txtNome" onChange={handleInputChange} />
                  </label>
              </div>

              <input type="submit" value="Salvar" />
              <div><p>Retornou: {ret}</p></div>
          </fieldset>
      </form>
  </div>
)
}

export default App;
